svg.script:
var paramArray = {
  size: 1,
  color: 0
};

window.onload = function(){
  size = parseInt(paramArray['size']);
  c = parseInt(paramArray['color']);
  color = "rgb("+[c / 65536 & 0xFF,c / 256 & 0xFF,c & 0xFF].join()+")"

  path = [["M0," + (size * 50)],
          ["L"+(size * 50 + 50)+",0"],
          ["L"+(size * 100 + 50)+","+(size * 50+50)],
          ["L"+(size * 50)+","+(size * 100 + 50)]
  ];
  for(i = 1;i <= size;i++){
    path[0][i] = ("H"+(i*50)+" V"+((size-i) * 50));
    path[1][i] = ("V"+(i*50)+" H"+((size+i+1) * 50));
    path[2][i] = ("H"+((size * 100 + 50)-(i*50))+" V"+((size+i+1) * 50));
    path[3][i] = ("V"+((size * 100 + 50)-(i*50))+" H"+((size-i) * 50));
  }

  obj = document.getElementById("area");
  obj.setAttribute("width", size * 100 + 50);
  obj.setAttribute("height", size * 100 + 50);
  obj.setAttribute("fill", color);
  obj.setAttribute("d", path.join());

};
